SELECT COUNT(*) FROM cardgame_question
AND (question LIKE CONCAT('%', #{q}, '%') OR answer LIKE CONCAT('%', #{q}, '%'))
AND subject = #{subject}

SELECT id, question, answer, subject, explanation_text, created_at
FROM cardgame_question
WHERE id = #{id}

mapper.update:
UPDATE cardgame_question
SET question = #{question},
	answer = #{answer},
	subject = #{subject},
	explanation_text = #{explanation_text}
WHERE id = #{id}
